function Calendar({ selectedDate, onDateSelect }) {
  const today = new Date();
  const [currentMonth, setCurrentMonth] = useState(today.getMonth());
  const [currentYear, setCurrentYear] = useState(today.getFullYear());

  const monthNames = [
    "ThÃ¡ng 1",
    "ThÃ¡ng 2",
    "ThÃ¡ng 3",
    "ThÃ¡ng 4",
    "ThÃ¡ng 5",
    "ThÃ¡ng 6",
    "ThÃ¡ng 7",
    "ThÃ¡ng 8",
    "ThÃ¡ng 9",
    "ThÃ¡ng 10",
    "ThÃ¡ng 11",
    "ThÃ¡ng 12",
  ];

  const days = useMemo(() => {
    const firstDay = new Date(currentYear, currentMonth, 1).getDay(); // 0=CN
    const daysInMonth = new Date(currentYear, currentMonth + 1, 0).getDate();
    const arr = [];

    for (let i = 0; i < firstDay; i++) arr.push(null);

    for (let d = 1; d <= daysInMonth; d++) {
      const dateObj = new Date(currentYear, currentMonth, d);
      const dateStr = toDateStr(dateObj);
      const isToday = dateStr === TODAY_STR;
      const isPast =
        dateObj <
        new Date(today.getFullYear(), today.getMonth(), today.getDate());
      const isSelected = selectedDate === dateStr;
      arr.push({ d, dateStr, isToday, isPast, isSelected });
    }

    const rem = arr.length % 7;
    if (rem) for (let i = 0; i < 7 - rem; i++) arr.push(null);

    return arr;
  }, [currentMonth, currentYear, selectedDate]);

  const previousMonth = () => {
    if (currentMonth === 0) {
      setCurrentMonth(11);
      setCurrentYear((y) => y - 1);
    } else setCurrentMonth((m) => m - 1);
  };
  const nextMonth = () => {
    if (currentMonth === 11) {
      setCurrentMonth(0);
      setCurrentYear((y) => y + 1);
    } else setCurrentMonth((m) => m + 1);
  };

  return (
    <div className="bg-white rounded-xl shadow-sm p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">Chá»n ngÃ y</h3>

      <div className="flex items-center justify-between mb-4">
        <button
          onClick={previousMonth}
          className="p-2 hover:bg-gray-100 rounded-lg"
          aria-label="ThÃ¡ng trÆ°á»›c"
        >
          <svg
            className="w-5 h-5"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M15 19l-7-7 7-7"
            />
          </svg>
        </button>
        <h4 className="text-lg font-semibold">
          {monthNames[currentMonth]}, {currentYear}
        </h4>
        <button
          onClick={nextMonth}
          className="p-2 hover:bg-gray-100 rounded-lg"
          aria-label="ThÃ¡ng sau"
        >
          <svg
            className="w-5 h-5"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth="2"
              d="M9 5l7 7-7 7"
            />
          </svg>
        </button>
      </div>

      <div className="grid grid-cols-7 gap-1 mb-2">
        {["CN", "T2", "T3", "T4", "T5", "T6", "T7"].map((w) => (
          <div
            key={w}
            className="text-center text-sm font-medium text-gray-500 py-2"
          >
            {w}
          </div>
        ))}
      </div>

      <div className="grid grid-cols-7 gap-1">
        {days.map((cell, idx) => {
          if (!cell) return <div key={`e-${idx}`} />;
          const { d, dateStr, isToday, isPast, isSelected } = cell;

          const base = "p-3 text-center rounded-lg transition-all select-none";
          const state = isPast
            ? "text-gray-400 cursor-not-allowed"
            : isSelected
            ? "bg-gradient-to-br from-emerald-600 to-amber-500 text-white shadow cursor-pointer"
            : isToday
            ? "ring-2 ring-amber-400 text-emerald-700 cursor-pointer"
            : "hover:bg-gray-100 cursor-pointer";

          return (
            <div
              key={dateStr}
              className={`${base} ${state}`}
              onClick={!isPast ? () => onDateSelect(dateStr) : undefined}
              title={dateStr}
            >
              {d}
            </div>
          );
        })}
      </div>
    </div>
  );
}

function TimeAndDuration({
