/* =========================
   MAIN: Booking (4 bÆ°á»›c)
========================= */
export default function Booking() {
  const [currentStep, setCurrentStep] = useState(1);
  const [selectedKey, setSelectedKey] = useState(null);
  const service = selectedKey ? SERVICES[selectedKey] : null;

  const [selectedDate, setSelectedDate] = useState(TODAY_STR);
  const [selectedTime, setSelectedTime] = useState("");
  const [duration, setDuration] = useState(service?.minHours ?? 2);

  const [customerInfo, setCustomerInfo] = useState({
    name: "",
    phone: "",
    email: "",
    address: "",
    notes: "",
    paymentMethod: "cash",
  });

  const subtotal = useMemo(
    () => (service ? service.price * duration : 0),
    [service, duration]
  );
  const discount = useMemo(
    () =>
      customerInfo.paymentMethod === "online" ? Math.round(subtotal * 0.05) : 0,
    [customerInfo.paymentMethod, subtotal]
  );
  const total = useMemo(
    () => Math.max(0, subtotal - discount),
    [subtotal, discount]
  );

  const [successCode, setSuccessCode] = useState("");

  const goNext = () => setCurrentStep((s) => Math.min(4, s + 1));
  const goBack = () => setCurrentStep((s) => Math.max(1, s - 1));

  const onServiceNext = () => {
    if (!selectedKey) return;
    // báº£o Ä‘áº£m duration tá»‘i thiá»ƒu theo service
    setDuration(SERVICES[selectedKey].minHours);
    goNext();
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const onTimeNext = () => {
    // validate á»Ÿ StepTime Ä‘Ã£ disable nÃºt; váº«n phÃ²ng há»:
    if (!selectedDate || !selectedTime) return;
    goNext();
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const onCustomerNext = () => {
    const { name, phone, address } = customerInfo;
    if (!name.trim() || !phone.trim() || !address.trim()) return;
    goNext();
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const onConfirm = async () => {
    // debug: kiá»ƒm tra token hiá»‡n cÃ³
    console.log("Token when booking:", localStorage.getItem("cm_token"));

    try {
      const res = await createBooking({
        CleanerId: 1,
        // gá»­i ISO tháº­t sá»± Ä‘á»ƒ .NET parse an toÃ n
        StartTime: new Date(`${selectedDate}T${selectedTime}:00`).toISOString(),
        DurationHours: duration,
        Price: total,
        Address: customerInfo.address,
        Notes: customerInfo.notes,
        PaymentMethod: customerInfo.paymentMethod,
      });

      setSuccessCode("CM" + String(res.id).padStart(6, "0"));
      setCurrentStep(99);
    } catch (err) {
      console.error("createBooking error:", {
        status: err?.response?.status,
        data: err?.response?.data,
      });
      alert(err?.response?.data || "Äáº·t lá»‹ch tháº¥t báº¡i");
    }
  };

  const resetAll = () => {
    setCurrentStep(1);
    setSelectedKey(null);
    setSelectedDate(TODAY_STR);
    setSelectedTime("");
    setDuration(2);
    setCustomerInfo({
      name: "",
      phone: "",
      email: "",
      address: "",
      notes: "",
      paymentMethod: "cash",
    });
    setSuccessCode("");
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  const pricingSummary = {
    serviceName: service?.name || "",
    date: selectedDate,
    time: selectedTime,
    duration,
    subtotal,
    discount,
    total,
  };

  return (
    <div className="min-h-screen bg-gray-50 text-gray-800">
      {currentStep >= 1 && currentStep <= 4 && (
        <ProgressSteps currentStep={currentStep} />
      )}

      <main className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 py-10">
        {currentStep === 1 && (
          <StepService
            selected={selectedKey}
            setSelected={setSelectedKey}
            onNext={onServiceNext}
          />
        )}

        {currentStep === 2 && (
          <StepTime
            service={service}
            selectedDate={selectedDate}
            setSelectedDate={setSelectedDate}
            selectedTime={selectedTime}
            setSelectedTime={setSelectedTime}
            duration={duration}
            setDuration={setDuration}
            totalPrice={subtotal}
            onNext={onTimeNext}
            onBack={goBack}
          />
        )}

        {currentStep === 3 && (
          <StepCustomer
            customerInfo={customerInfo}
            setCustomerInfo={setCustomerInfo}
            pricing={pricingSummary}
            onNext={onCustomerNext}
            onBack={goBack}
          />
        )}

        {currentStep === 4 && (
          <StepConfirm
            service={service}
            date={selectedDate}
            time={selectedTime}
            duration={duration}
            customer={customerInfo}
            pricing={pricingSummary}
            onBack={goBack}
            onConfirm={onConfirm}
          />
        )}

        {currentStep === 99 && (
          <Success
            code={successCode}
            onNew={resetAll}
            onHome={() => {
              /* Link trong component */
            }}
          />
        )}
      </main>
    </div>
  );
}
